svg.script:
		document.setStyleHighlight = function() 
		{
			document.getElementById("yaha_icon_elements").style.stroke = "#0080FF";
    	}		

		document.setStyleInverted = function() 
		{
			document.getElementById("yaha_icon_elements").style.stroke = "#FFFFFF";
    	}		

		//default settings of this symbol
		document.resetStyle = function() 
		{
			document.getElementById("yaha_icon_elements").style.stroke = "#808080";
			document.getElementById("yaha_icon_elements").style['stroke-width'] = 5;
			document.getElementById("yaha_icon_elements").style['stroke-linecap'] = "round";
			document.getElementById("yaha_icon_elements").style.fill = "none";
    	}
