svg.script:
		document.setStyleHighlight = function() 
		{
			document.getElementById("yaha_icon_style").style.stroke = "#0C40FF";
			document.getElementById("yaha_icon_style").style.fill = "#0C40FF";
			document.getElementById("yaha_icon_elements").style.fill = "#0C40FF";
			document.getElementById("yaha_icon_elements").style.stroke = "#0C40FF";
		}		

		document.setStyleInverted = function() 
		{
			document.getElementById("yaha_icon_style").style.stroke = "#0C40FF";
			document.getElementById("yaha_icon_style").style.fill = "#0C40FF";


			document.getElementById("yaha_icon_frame").style['fill-opacity'] = 1;
			document.getElementById("yaha_icon_frame").style['stroke-width'] = 2;

			document.getElementById("yaha_icon_elements").style['fill-opacity'] = 1;
			document.getElementById("yaha_icon_elements").style['stroke-width'] = 1;
			document.getElementById("yaha_icon_elements").style.fill = "#FFFFFF";
			document.getElementById("yaha_icon_elements").style.stroke = "#FFFFFF";
    	}		


		//default settings of this symbol
		document.resetStyle = function() 
		{
			document.getElementById("yaha_icon_style").style.stroke = "#808080";
			document.getElementById("yaha_icon_style").style.fill = "#808080";


			document.getElementById("yaha_icon_frame").style['fill-opacity'] = 0;
			document.getElementById("yaha_icon_frame").style['stroke-width'] = 2;

			document.getElementById("yaha_icon_elements").style['fill-opacity'] = 1;
			document.getElementById("yaha_icon_elements").style['stroke-width'] = 1;
			document.getElementById("yaha_icon_elements").style.fill = "#808080";
			document.getElementById("yaha_icon_elements").style.stroke = "#808080";
		}
